import React, { useState } from "react";
//import "bootstrap/dist/css/bootstrap.min.css";
import { Carousel } from "react-bootstrap";
import Image from "../../elements/Image";

const pics = [
  "https://preply.com/wp-content/uploads/2018/08/unnamed.png",
  "https://w7.pngwing.com/pngs/133/892/png-transparent-graph-of-a-function-statistics-business-chart-number-telemarketing-angle-text-people.png",
];

const Slide = () => {
  const [index, setIndex] = useState(0);

  const handleSelect = (selectedIndex, e) => {
    setIndex(selectedIndex);
  };
  return (
    <div>
      <Carousel
        activeIndex={index}
        onSelect={handleSelect}
        className="mt-5 bg-black"
        // nextIcon={navIcon("carousel-control-next-icon")}
        // prevIcon={navIcon("carousel-control-prev-icon")}
        // style={{ marginTop: '1000px' }}
      >
        <Carousel.Item>

          <Image
            className="has-shadow"
            src={"https://preply.com/wp-content/uploads/2018/08/unnamed.png"}
            alt="Hero"
            width={400}
            height={300}
          />
        </Carousel.Item>
      </Carousel>
    </div>
  );
};

export default Slide;
